function isInViewport(t){const e=t.getBoundingClientRect();return e.top>=0&&e.left>=0&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)&&e.right<=(window.innerWidth||document.documentElement.clientWidth)}function loadImage(t){const e=t.getAttribute("data-src");e&&(t.setAttribute("src",e),t.removeAttribute("data-src"))}function handleScroll(){document.querySelectorAll("img[data-src]").forEach((t=>{isInViewport(t)&&loadImage(t)}))}document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll("img[data-src]").forEach((t=>{isInViewport(t)&&loadImage(t)}))})),window.addEventListener("scroll",handleScroll);