function isInViewport(t){const e=t.getBoundingClientRect();return e.bottom>=0&&e.right>=0&&e.top<=(window.innerHeight||document.documentElement.clientHeight)&&e.left<=(window.innerWidth||document.documentElement.clientWidth)}function loadImage(t){const e=t.getAttribute("data-src");e&&(t.setAttribute("src",e),t.removeAttribute("data-src"))}function handleScroll(){document.querySelectorAll("img[data-src]").forEach((t=>{isInViewport(t)&&loadImage(t)}))}document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll("img[data-src]").forEach((t=>{isInViewport(t)&&loadImage(t)}))})),window.addEventListener("scroll",handleScroll);